import React, { useState } from "react";
import * as AddUser from "../AddUser.graphql";
import { useMutation } from "@apollo/react-hooks";

export default (props) => {
  let loginInp;
  let passwordInp;

  const switchfun = (e) => {
    let id = e.target.id;
    if (id === "reg") {
      document.getElementById("auth").classList.remove("active");
      document.getElementById("reg").classList.add("active");
    }
    if (id === "auth") {
      document.getElementById("reg").classList.remove("active");
      document.getElementById("auth").classList.add("active");
    }
  };
  const [addUser] = useMutation(AddUser);

  function loginfun(e) {
    e.preventDefault();
    if (document.getElementById("reg").className === "active") {
      if (loginInp.value != "" && passwordInp.value != "") {
        addUser({
          variables: { username: loginInp.value, password: passwordInp.value },
        });
        props.getUser({
          variables: { username: loginInp.value, password: passwordInp.value },
        });
      }
    }
    if (document.getElementById("auth").className === "active") {
      props.getUser({
        variables: { username: loginInp.value, password: passwordInp.value },
      });
    }
    loginInp.value = "";
    passwordInp.value = "";
  }

  return (
    <div className="box-auth">
      <form method="GET" onSubmit={loginfun}>
        <div className="switch">
          <div href="" id="auth" className="active" onClick={switchfun}>
            sing in
          </div>
          <div href="" id="reg" onClick={switchfun}>
            sin up
          </div>
        </div>
        <input
          placeholder="login"
          ref={(node) => {
            loginInp = node;
          }}
        ></input>
        <input
          placeholder="password"
          ref={(node) => {
            passwordInp = node;
          }}
        ></input>
        <div className="submit">
          <input type="submit" value="submit"></input>
          <span className="bg"></span>
        </div>
      </form>
    </div>
  );
};
